function extractCompleteObjectsFromFirstArray(text) {
  const start = text.indexOf('[');
  if (start < 0) return [];
  let i = start + 1;
  const objs = [];
  let inStr = false, quote = null, esc = false, depth = 0, objStart = -1;

  for (; i < text.length; i++) {
    const ch = text[i];

    if (inStr) {
      if (esc) { esc = false; continue; }
      if (ch === '\\') { esc = true; continue; }
      if (ch === quote) { inStr = false; quote = null; continue; }
      continue;
    }

    if (ch === '"' || ch === "'") { inStr = true; quote = ch; continue; }

    if (ch === '{') {
      if (depth === 0) objStart = i;
      depth++;
    } else if (ch === '}') {
      depth--;
      if (depth === 0 && objStart >= 0) {
        const slice = text.slice(objStart, i + 1);
        try { objs.push(JSON.parse(slice)); } catch {}
        objStart = -1;
      }
    } else if (ch === ']') {
      // –∫–æ–Ω–µ—Ü –º–∞—Å—Å–∏–≤–∞ ‚Äî –≤—ã—Ö–æ–¥–∏–º
      break;
    }
  }
  return objs;
}

